jQuery(document).ready(function () {
    if (jQuery('.flexslider').length && jQuery()) {
        jQuery('.flexslider').flexslider({
            animation: 'slide',
            direction: 'vertical',
            controlNav: true,
            directionNav: false,
            animationLoop: true,
            controlsContainer: "",
            pauseOnHover: true,
            nextText: "&rsaquo;",
            prevText: "&lsaquo;",
            keyboardNav: true,
            slideshowSpeed: 5000,
            animationSpeed: 500,
            start: function (slider) {
                slider.removeClass('loading');
            }

        });
    }
